import {takeLatest,put,all,call} from 'redux-saga/effects';

import UserActionType from "./userType";

import {googleProvider,auth,creatUserProfileDocument} from "../../firebase/firebase.utils";

export function* signInWithGoogle() {
    try{
        const userRef = auth.signInWithPopup(googleProvider);
        console.log(userRef)
    }catch (error) {
        
    }
}

export function* onGoogleSignInStart() {
 yield takeLatest(UserActionType.GOOGLE_SIGN_IN_START,)
}

export function* userSaga() {
    yield all([call(onGoogleSignInStart)])
}
