import React from "react";
import { connect } from 'react-redux';
import { createStructuredSelector} from "reselect";

import CheckoutItem from "../../components/checkout-item/checkoutItem";
import StripeButton from "../../components/stripe-button/stripeButton";

import {selectCartItem,selectCartTotal} from "../../redux/cart/cartSelector";

import './checkput.scss';
import {CheckoutPageContainer,CheckoutHeaderContainer,HeaderBlockContainer,TotalContainer,WarningContainer} from "./checkout.styles";

const Checkout = ({cartItems,total}) =>{
    return(
        <CheckoutPageContainer>
            <CheckoutHeaderContainer>
                <HeaderBlockContainer>
                    <span>Product</span>
                </HeaderBlockContainer>
                <HeaderBlockContainer>
                    <span>Description</span>
                </HeaderBlockContainer>
                <HeaderBlockContainer>
                    <span>Quantity</span>
                </HeaderBlockContainer>
                <HeaderBlockContainer>
                    <span>Price</span>
                </HeaderBlockContainer>
                <HeaderBlockContainer>
                    <span>Remove</span>
                </HeaderBlockContainer>
            </CheckoutHeaderContainer>
            {
                cartItems.map(cartItem =>(
                    <CheckoutItem key={cartItem.id} cartItem={cartItem}/>
                ))
            }
            <TotalContainer>TOTAL : ${total}</TotalContainer>
            <WarningContainer>
                *Please use the following credit card for payments*
                <br/>
                4242 4242 4242 4242 -Exp:01/20 CVV:123
            </WarningContainer>
            <StripeButton price={total}/>

        </CheckoutPageContainer>
    )
}
const mapStateToProps = createStructuredSelector({
    cartItems:selectCartItem,
    total:selectCartTotal
})

export default connect(mapStateToProps)(Checkout);
