import React from "react";
import { Route } from 'react-router-dom';
import {connect} from 'react-redux';
import {createStructuredSelector} from "reselect";

import {selectIsCollectionFetching,selectCollectionIsLoaded} from "../../redux/shop/shopSelector";
import {fetchCollectionsStartAsync} from "../../redux/shop/shopAction";


import withSpinner from "../../components/with-spinner/withSpinner";
import CollectionOverview from '../../components/collection-overview/collectionOverview';
import CollectionPage from "../collection/collection";

const CollectionOverviewWithSpinner = withSpinner(CollectionOverview);
const CollectionPageWithSpinner = withSpinner(CollectionPage);



class ShopPage extends React.Component{
   componentDidMount() {
       const {updateCollections} = this.props;
       updateCollections();
   }

    render() {
        const {match,isCollectionFetching,isCollectionLoaded} = this.props;
        return(
            <div className='shop-page'>
                <Route exact path={`${match.path}`}
                       render={props => <CollectionOverviewWithSpinner isLoading={isCollectionFetching} {...props} />}
                />
                <Route path={`${match.path}/:collectionId`}
                       render={props => <CollectionPageWithSpinner isLoading={!isCollectionLoaded} {...props}/>}
                />
            </div>
        )
    }

}

const mapStateToProps = createStructuredSelector({
    isCollectionFetching: selectIsCollectionFetching,
    isCollectionLoaded : selectCollectionIsLoaded
})

const mapDispatchToProps = dispatch =>({
    updateCollections: () => dispatch(fetchCollectionsStartAsync())
})




export default connect(mapStateToProps,mapDispatchToProps)(ShopPage);
