import {CartActionTypes} from "./cartTypes";

const INITIAL_STATE = {
    hidden:true,
    
}

const cartReducer = (state=INITIAL_STATE, action) =>{
    switch (action.type) {
        case CartActionTypes.TOGGLE_CART_HIDDEN:
            return{
                ...state,
                hidden: !state.hidden
            }
        case CartActionTypes.ADD_ITEM:
            return {
                ...state,

            }
        default:
            return state;

    }
}
export default cartReducer;
