import {takeLatest,put,all,call} from 'redux-saga/effects';

import UserActionType from "./userType";

import {googleProvider,auth,creatUserProfileDocument} from "../../firebase/firebase.utils";

import {googleSignInFailure,googleSignInSuccess} from "./userAction";

export function* signInWithGoogle() {
    try{
        const {user} = yield auth.signInWithPopup(googleProvider);
        const userRef = yield call(creatUserProfileDocument,user);
        const userSnapshot = yield userRef.get();
        yield put(googleSignInSuccess({id:userSnapshot.id , ...userSnapshot.data()}))
    }catch (error) {
        yield put(googleSignInFailure(error))
    }
}

export function* onGoogleSignInStart() {
 yield takeLatest(
     UserActionType.GOOGLE_SIGN_IN_START,
     signInWithGoogle
 )
}

export function* userSaga() {
    yield all([call(onGoogleSignInStart)])
}
